mapper.select:
SELECT b.bay_post_no, b.title, b.regdate, b.id, m.name
FROM(
SELECT row_number() over(ORDER BY bay_post_no DESC) as rnum,
bay_post_no, title, regdate, id
FROM bay_post
)b, bay_member m
WHERE b.id = m.id AND rnum BETWEEN #{startRowNumber}
AND #{endRowNumber}
ORDER BY bay_post_no DESC

mapper.insert:

SELECT board_type_no FROM board_type WHERE type=#{type}

INSERT INTO bay_post(bay_post_no,title,content,regdate,id,board_type_no,subject_no)
VALUES (bay_post_seq.nextval,#{title},#{content},sysdate,#{id},#{boardTypeNo},#{subjectNo})
