mapper.select:
SELECT count(*)
FROM receive_message
WHERE receive_id=#{value}

SELECT count(*)
FROM send_message
WHERE send_id=#{value}

SELECT rnum,message_no,receive_id,send_id,regdate,title
FROM
	(SELECT row_number() over(ORDER BY regdate DESC) AS rnum,message_no,receive_id,send_id,title,regdate 
	FROM 
		(SELECT receive_message_no as message_no,send_id,receive_id,title,regdate 
		 FROM receive_message
		 WHERE receive_id=#{id}
		 UNION ALL
		 SELECT send_message_no as message_no,send_id,receive_id,title,regdate 
		 FROM SEND_MESSAGE
		 WHERE send_id=#{id}
		)
	)
WHERE rnum BETWEEN #{pagingBean.startRowNumber} AND #{pagingBean.endRowNumber}

SELECT send_id,receive_id,regdate,title,content
,status,send_message_no FROM receive_message WHERE receive_message_no=#{messageNo}
,receive_flag,receive_date FROM send_message WHERE send_message_no=#{messageNo}

mapper.update:
UPDATE send_message 
SET receive_flag=1,receive_date=sysdate 
WHERE send_message_no=#{value}

UPDATE receive_message 
SET status=1 
WHERE receive_message_no=#{value}
