mapper.select:
SELECT b.bay_post_no, b.title, b.regdate, b.id, b.board_type_no, m.name
FROM(
SELECT row_number() over(ORDER BY bay_post_no DESC) as rnum,
bay_post_no, title, regdate, id, board_type_no
FROM bay_post
WHERE board_type_no=#{boardTypeNo}
)b, bay_member m
WHERE b.id = m.id AND rnum BETWEEN #{pagingBean.startRowNumber}
AND #{pagingBean.endRowNumber}
ORDER BY bay_post_no DESC

SELECT b.title,b.content,b.regdate,b.id,b.board_type_no,b.bay_post_no
FROM bay_post b
WHERE b.bay_post_no = #{value}

mapper.update:
UPDATE bay_post
SET title = #{title}, content = #{content}
WHERE bay_post_no = #{bayPostNo}
